#include <stdio.h>

int gcd(int a, int b){
    while(b){ int t=b; b=a%b; a=t; }
    return a;
}
int main(){ printf("GCD = %d\n", gcd(48,18)); return 0; }

#include <stdio.h>

size_t my_strlen(const char *s){ const char *p=s; while(*p) p++; return p-s; }
char* my_strcpy(char *dest, const char *src){ char *d=dest; while((*d++ = *src++)); return dest; }
int main(){ char s[100] = "hello"; printf("len=%zu\n", my_strlen(s)); char t[100]; my_strcpy(t,s); printf("t=%s\n",t); }

#include <stdio.h>
#include <stdlib.h>
int main(){ int n=5; int *a = malloc(n * sizeof *a); if(!a){ perror("malloc"); return 1; }
for(int i=0;i<n;i++) a[i]=i*i;
for(int i=0;i<n;i++) printf("%d ", a[i]);
free(a);
return 0; }

#include <stdio.h>
#include <math.h>
typedef struct { double x,y; } Point;
double dist(const Point *a,const Point *b){ double dx=a->x-b->x; double dy=a->y-b->y; return sqrt(dx*dx+dy*dy); }
int main(){ Point p1={0,0}, p2={3,4}; printf("dist=%.2f\n", dist(&p1,&p2)); }

#include <stdio.h>
#include <stdlib.h>
int main(){ FILE *in=fopen("input.txt","r"); if(!in){ perror("fopen"); return 1; }
FILE *out=fopen("out.txt","w"); if(!out){ perror("fopen out"); fclose(in); return 1; }
char buf[512]; int ln=1; while(fgets(buf,sizeof buf,in)){ fprintf(out, "%4d: %s", ln++, buf); }
fclose(in); fclose(out); return 0; }

#include <stdio.h>
#include <stdlib.h>
typedef struct Node{ int val; struct Node *next; } Node;
Node* push(Node *head,int v){ Node *n=malloc(sizeof *n); n->val=v; n->next=head; return n; }
Node* delete_val(Node *head,int v){ Node **cur=&head; while(*cur){ if((*cur)->val==v){ Node *tmp=*cur; *cur=(*cur)->next; free(tmp); break; } cur=&(*cur)->next; } return head; }
void printlist(Node *h){ while(h){ printf("%d -> ", h->val); h=h->next; } printf("NULL\n"); }
int main(){ Node *head=NULL; head=push(head,3); head=push(head,5); printlist(head); head=delete_val(head,3); printlist(head); // free remaining
 while(head){ Node *t=head; head=head->next; free(t);} }
